function checkpoint(chkptf, pars, t, t_last_diag, t_last_chkpt, wq, wdq_dt, tlev)%CHECKPOINT   Writes list of variables to file.%    CHECKPOINT(chkptf, pars, t, t_last_diag, wq, wdq_dt, tlev) writes%    the variables pars, t, t_last_diag, etc. to the Matlab file%    chkptf.mat.  chkptf = [chkptf, '.mat'];  save(chkptf, 'pars', 't', 't_last_diag', 't_last_chkpt', ...       'wq', 'wdq_dt', 'tlev', '-mat')   disp(sprintf('Writing checkpoint file: %s', chkptf))