function init_figure()% INIT_FIGURE  Initialize figure.    fwidth       = 7.9/2.54;  fheight      = fwidth / 1.5;  line_width   = .15;          font_size    = 6;        % set properties of plots  clf  set(gcf, 'Units', 'inches', 'PaperUnits', 'inches')  psize     = get(gcf, 'PaperSize');  fpos      = get(gcf, 'Position');  ppos      = [(psize(1) - fwidth)/2, (psize(2) - fheight)/2, fwidth, fheight];  fpos      = [fpos(1) fpos(2) fpos(3) fpos(3)*fheight/fwidth];  set(gcf, 'PaperPosition', ppos, ...	   'Position', fpos, ...	   'DefaultLineLineWidth', line_width, ...	   'DefaultLineMarkerSize', font_size, ...	   'DefaultAxesLineWidth', line_width, ...	   'DefaultAxesFontSize',  font_size, ...	   'DefaultAxesLayer', 'top', ...	   'DefaultAxesPosition', [.135 .15 .805 .7] )  