%init          = 0;             % initialize?init          = 1;             % initialize?pars.relax    = 0.16;          % Inverse relaxation time scale of forcingpars.beta     = 1;             % Mean potential vorticity gradient  pars.Gamma    = 1;pars.widthx   = 16*pi;         % Channel lengthpars.widthy   = 4*pi;          % Channel widthpars.ubar     = pars.widthy/4; % Background flow s.t. u0=0 at channel centerpars.dt_diag  = .5;            % Time interval for saving tracer fieldpars.dt_chkpt = 75;            % Time interval for checkpointing of simulation% Integration intervalt_max         = max(1300, 550+1.5/pars.relax); %t_max         = 5;% Data directory%pars.fdir     = '/home/tapio/data/ptjet/';pars.fdir     = '../data/';%pars.fdir     = '/home/tapio/matlab/ptjet/';% ALPHA: FT(advected variable) = -(K^alpha) * FT(streamfunction)%        alpha = 2 for barotropic vorticity equation%        alpha = 1 for surface quasi-geostrophypars.alpha    = 2;   % time step and damping of highest wavenumberif pars.relax <= 0.002  pars.dt    = 1/170;  pars.nvisc = 1;   t0         = 50 + 1.5/pars.relax;elseif pars.relax > 0.002 & pars.relax <= 0.015  pars.dt    = 1/160;  pars.nvisc = 1;   t0         = 50 + 2/pars.relax;elseif pars.relax > 0.015 & pars.relax <= 0.06  pars.dt    = 1/190;  pars.nvisc = 2;   t0         = 150;elseif pars.relax > 0.06  pars.dt    = 1/210;  pars.nvisc = 2;   t0         = 150;end% dimension of fields in wavenumber spacepars.nkx     = 128;pars.nky     = 254;% dimension of fields in grid space (large enough to ensure% unaliased products)pars.nx      = 384;pars.ny      = 384;